SELECT MIN(t.title) AS movie_title FROM 'company_name.parquet' cn, 'keyword.parquet' k, 'movie_companies.parquet' mc, 'movie_keyword.parquet' mk, 'title.parquet' t WHERE cn.country_code ='[nl]' AND k.keyword ='character-name-in-title' AND cn.id = mc.company_id AND mc.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND mc.movie_id = mk.movie_id;
