SELECT MIN(chn.name) AS character, MIN(t.title) AS movie_with_american_producer FROM external('char_name.parquet') chn, external('cast_info.parquet') ci, external('company_name.parquet') cn, external('company_type.parquet') ct, external('movie_companies.parquet') mc, external('role_type.parquet') rt, external('title.parquet') t WHERE ci.note  like '%(producer)%' AND cn.country_code  = '[us]' AND t.production_year > 1990 AND t.id = mc.movie_id AND t.id = ci.movie_id AND ci.movie_id = mc.movie_id AND chn.id = ci.person_role_id AND rt.id = ci.role_id AND cn.id = mc.company_id AND ct.id = mc.company_type_id;
