SELECT MIN(mi.info) AS budget, MIN(t.title) AS unsuccsessful_movie FROM external('company_name.parquet') cn, external('company_type.parquet') ct, external('info_type.parquet') it1, external('info_type.parquet') it2, external('movie_companies.parquet') mc, external('movie_info.parquet') mi, external('movie_info_idx.parquet') mi_idx, external('title.parquet') t WHERE cn.country_code ='[us]' AND ct.kind  is not NULL and (ct.kind ='production companies' or ct.kind = 'distributors') AND it1.info ='budget' AND it2.info ='bottom 10 rank' AND t.production_year >2000 AND (t.title LIKE 'Birdemic%' OR t.title LIKE '%Movie%') AND t.id = mi.movie_id AND t.id = mi_idx.movie_id AND mi.info_type_id = it1.id AND mi_idx.info_type_id = it2.id AND t.id = mc.movie_id AND ct.id = mc.company_type_id AND cn.id = mc.company_id AND mc.movie_id = mi.movie_id AND mc.movie_id = mi_idx.movie_id AND mi.movie_id = mi_idx.movie_id;
