SELECT MIN(t.title) AS movie_title FROM external('company_name.parquet') cn, external('keyword.parquet') k, external('movie_companies.parquet') mc, external('movie_keyword.parquet') mk, external('title.parquet') t WHERE cn.country_code ='[sm]' AND k.keyword ='character-name-in-title' AND cn.id = mc.company_id AND mc.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND mc.movie_id = mk.movie_id;
