SELECT MIN(an1.name) AS costume_designer_pseudo, MIN(t.title) AS movie_with_costumes FROM external('aka_name.parquet') an1, external('cast_info.parquet') ci, external('company_name.parquet') cn, external('movie_companies.parquet') mc, external('name.parquet') n1, external('role_type.parquet') rt, external('title.parquet') t WHERE cn.country_code ='[us]' AND rt.role ='costume designer' AND an1.person_id = n1.id AND n1.id = ci.person_id AND ci.movie_id = t.id AND t.id = mc.movie_id AND mc.company_id = cn.id AND ci.role_id = rt.id AND an1.person_id = ci.person_id AND ci.movie_id = mc.movie_id;
