SELECT MIN(chn.name) AS character, MIN(t.title) AS russian_mov_with_actor_producer FROM umbra.parquetview('char_name.parquet') AS chn, umbra.parquetview('cast_info.parquet') AS ci, umbra.parquetview('company_name.parquet') AS cn, umbra.parquetview('company_type.parquet') AS ct, umbra.parquetview('movie_companies.parquet') AS mc, umbra.parquetview('role_type.parquet') AS rt, umbra.parquetview('title.parquet') AS t WHERE ci.note  like '%(producer)%' AND cn.country_code  = '[ru]' AND rt.role  = 'actor' AND t.production_year > 2010 AND t.id = mc.movie_id AND t.id = ci.movie_id AND ci.movie_id = mc.movie_id AND chn.id = ci.person_role_id AND rt.id = ci.role_id AND cn.id = mc.company_id AND ct.id = mc.company_type_id;
