SELECT MIN(n.name) AS member_in_charnamed_movie FROM umbra.parquetview('cast_info.parquet') AS ci, umbra.parquetview('company_name.parquet') AS cn, umbra.parquetview('keyword.parquet') AS k, umbra.parquetview('movie_companies.parquet') AS mc, umbra.parquetview('movie_keyword.parquet') AS mk, umbra.parquetview('name.parquet') AS n, umbra.parquetview('title.parquet') AS t WHERE k.keyword ='character-name-in-title' AND n.name  LIKE '%B%' AND n.id = ci.person_id AND ci.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND t.id = mc.movie_id AND mc.company_id = cn.id AND ci.movie_id = mc.movie_id AND ci.movie_id = mk.movie_id AND mc.movie_id = mk.movie_id;
