SELECT MIN(t.title) AS movie_title FROM umbra.parquetview('company_name.parquet') AS cn, umbra.parquetview('keyword.parquet') AS k, umbra.parquetview('movie_companies.parquet') AS mc, umbra.parquetview('movie_keyword.parquet') AS mk, umbra.parquetview('title.parquet') AS t WHERE cn.country_code ='[sm]' AND k.keyword ='character-name-in-title' AND cn.id = mc.company_id AND mc.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND mc.movie_id = mk.movie_id;
