SELECT MIN(mi_idx.info) AS rating, MIN(t.title) AS movie_title FROM umbra.parquetview('info_type.parquet') AS it, umbra.parquetview('keyword.parquet') AS k, umbra.parquetview('movie_info_idx.parquet') AS mi_idx, umbra.parquetview('movie_keyword.parquet') AS mk, umbra.parquetview('title.parquet') AS t WHERE it.info ='rating' AND k.keyword  like '%sequel%' AND mi_idx.info  > '5.0' AND t.production_year > 2005 AND t.id = mi_idx.movie_id AND t.id = mk.movie_id AND mk.movie_id = mi_idx.movie_id AND k.id = mk.keyword_id AND it.id = mi_idx.info_type_id;
