SELECT MIN(a1.name) AS writer_pseudo_name, MIN(t.title) AS movie_title FROM umbra.parquetview('aka_name.parquet') AS a1, umbra.parquetview('cast_info.parquet') AS ci, umbra.parquetview('company_name.parquet') AS cn, umbra.parquetview('movie_companies.parquet') AS mc, umbra.parquetview('name.parquet') AS n1, umbra.parquetview('role_type.parquet') AS rt, umbra.parquetview('title.parquet') AS t WHERE cn.country_code ='[us]' AND rt.role ='writer' AND a1.person_id = n1.id AND n1.id = ci.person_id AND ci.movie_id = t.id AND t.id = mc.movie_id AND mc.company_id = cn.id AND ci.role_id = rt.id AND a1.person_id = ci.person_id AND ci.movie_id = mc.movie_id;
